mapper.select:
select nvl(COUNT(*), 0)
	from buy_list a right outer join posts b
	on title = b.title where a.id LIKE #{id};

SELECT goal_amount, current_amount, status
FROM posts
WHERE posts_num = #{posts_num}

mapper.update:
UPDATE posts
SET current_amount = #{current_amount}, status = #{status}
WHERE posts_num = #{posts_num}
