mapper.select:
SELECT COUNT(*) FROM MEMBER
 WHERE ID = #{id}
   AND PASSWORD = #{password}

SELECT PASSWORD
FROM MEMBER
WHERE ID = #{id}
  AND EMAIL = #{email}

SELECT ID, PASSWORD, NICKNAME, NAME, ADDRESS1, ROLE, EMAIL, PHONE, DONG, ADDRESS2, ZIP_CODE
FROM MEMBER
WHERE ID = #{id}
  AND PASSWORD = #{password}
